
public class Ex05 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		gugu(2);
		gugu(5);
		gugu(6);
		gugu(8);
	}
	public static void gugu(int dan) {
		for(int i = 1; i <= 9; i++) {
			System.out.println(dan + " * " + i + " = " + dan * i);
		}
	}

}
